    //   let url = 'https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=41f0d47bd4d047b9a35cb89d64ce966f&page=${this.state.page + 1}&pageSize=${this.props.pageSize}';
